CREATE TABLE IF NOT EXISTS accident_history (accident_id VARCHAR(50) PRIMARY KEY, company_name VARCHAR(100), site_name VARCHAR(100), acci_time TIMESTAMP, acci_location VARCHAR(255), is_contractor BOOLEAN, victim_belong VARCHAR(100), accident_type_level1 VARCHAR(20), accident_type_level2 VARCHAR(50), death_count INTEGER, injured_count INTEGER, damage_cost INTEGER, damage_severity VARCHAR(50), misc_classification VARCHAR(50), acci_summary TEXT, direct_cause TEXT, root_cause TEXT, injury_location_detail TEXT, corrective_actions TEXT, victim_return_date TIMESTAMP, investigation_status VARCHAR(20), investigation_start_time TIMESTAMP, investigation_end_time TIMESTAMP, investigator_signature VARCHAR(100), legal_report_flag VARCHAR(10), created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP);
