CREATE TABLE IF NOT EXISTS investigation_report (accident_id VARCHAR(50) PRIMARY KEY REFERENCES occurrence_report(accident_id), investigation_start_time TIMESTAMP, investigation_end_time TIMESTAMP, investigation_team_lead VARCHAR(100), investigation_team_members TEXT, investigation_location VARCHAR(255), original_global_accident_no VARCHAR(50), investigation_global_accident_no VARCHAR(50), original_accident_id VARCHAR(50), investigation_accident_id VARCHAR(50), original_acci_time TIMESTAMP, investigation_acci_time TIMESTAMP, original_acci_location VARCHAR(255), investigation_acci_location VARCHAR(255), original_accident_type_level1 VARCHAR(20), investigation_accident_type_level1 VARCHAR(20), original_accident_type_level2 VARCHAR(50), investigation_accident_type_level2 VARCHAR(50), original_acci_summary TEXT, investigation_acci_summary TEXT, original_acci_detail TEXT, investigation_acci_detail TEXT, original_victim_count INTEGER, investigation_victim_count INTEGER, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP);
