CREATE TABLE IF NOT EXISTS victims (victim_id SERIAL PRIMARY KEY, accident_id VARCHAR(50) NOT NULL, name VARCHAR(100), age INTEGER, belong VARCHAR(100), duty VARCHAR(100), injury_type VARCHAR(100), ppe_worn VARCHAR(100), first_aid TEXT, birth_date TIMESTAMP, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, FOREIGN KEY (accident_id) REFERENCES occurrence_report(accident_id) ON DELETE CASCADE);
